const LANGUAGES = ["FR", "EN", "DE", "ES", "IT", "PT", "NL", "PL", "RU", "JA", "ZH", "KO", "AR", "TR", "SV", "DA", "FI", "NO", "CS", "EL", "HU", "RO", "SK", "BG", "UK", "ID"];

// POST - Insérer toutes les traductions
export async function POST() {
  try {
    let count = 0;

    for (const [key, translations] of Object.entries(TRANSLATIONS)) {
      for (const lang of LANGUAGES) {
        const value = translations[lang];
        if (!value) continue;

        // Chercher si la traduction existe déjà
        const existing = await prisma.translation.findFirst({
          where: {
            key,
            language: lang,
            type: "GLOBAL",
            siteId: null,
          },
        });

        if (existing) {
          await prisma.translation.update({
            where: { id: existing.id },
            data: { value },
          });
        } else {
          await prisma.translation.create({
            data: {
              key,
              language: lang,
              value,
              type: "GLOBAL",
            },
          });
        }
        count++;
      }
    }

    return NextResponse.json({ 
      success: true, 
      count,
      keys: Object.keys(TRANSLATIONS).length,
      languages: LANGUAGES.length,
    });
  } catch (error) {
    console.error("Error seeding translations:", error);
    return NextResponse.json({ error: "Failed to seed translations" }, { status: 500 });
  }
}
